mapper.select:
select a.id,u.name as 'owner',a.name,a.start_date,a.end_date,a.cost,a.description, a.create_time,u1.name as 'create_by',a.edit_time,u2.name as 'edit_by' from tbl_activity a join tbl_user u on a.owner = u.id join tbl_user u1 on a.create_by = u1.id left join tbl_user u2 on a.edit_by = u2.id
and a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}
order by a.create_time desc limit #{beginNo},#{pageSize}

select count(*) from tbl_activity a join tbl_user u on a.owner = u.id join tbl_user u1 on a.create_by = u1.id left join tbl_user u2 on a.edit_by = u2.id
and a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}

mapper.delete:
delete from tbl_activity where id
    in

    #{id}

